// src/modules/counter.js

const SUBMIT = "SUBMIT";

export const submit = () => {
  return {
    type: SUBMIT,
    newTodo,
  };
};

// 초기 상태값
const initialState = {
  lists: [
    {
      id: 0,
      title: "리액트 공부하기",
      desc: "리액트 기초를 공부해봅시다",
      isDone: false,
    },
    {
      id: 1,
      title: "리액트 알아보기",
      desc: "리액트 기초를 알아봅시다",
      isDone: true,
    },
  ],
};

// 리듀서
const addTodoList = (state = initialState, action) => {
  console.log(action);
  switch (action.type) {
    case "SUBMIT":
      return {
        lists: [
          {
            id: 0,
            title: "리액트 어렵지 않다",
            desc: "리액트 기초를 공부해봅시다",
            isDone: false,
          },
          {
            id: 1,
            title: "리액트 단지 익숙치 않을 뿐이다.",
            desc: "리액트 기초를 알아봅시다",
            isDone: true,
          },
        ],
      };

    default:
      return state;
  }
};

// 모듈파일에서는 리듀서를 export default 한다.
export default addTodoList;
