import List from "./Lists";

const ListContainer = ({ todoLists, deleteTodoList, onChangeDone }) => {
  console.log(todoLists);

  const WorkingLists = () => {
    todoLists
      .filter((todoList) => todoList.isDone === false)
      .map((todoList) => {
        return (
          <List
            handleDelete={deleteTodoList}
            handleDone={onChangeDone}
            todoList={todoList}
            key={todoList.id}
          ></List>
        );
      });
  };

  const DoneLists = () => {
    todoLists
      .filter((todoList) => todoList.isDone === true)
      .map((todoList) => {
        return (
          <List
            handleDelete={deleteTodoList}
            handleDone={onChangeDone}
            todoList={todoList}
            key={todoList.id}
          ></List>
        );
      });
  };

  return (
    <div className="list-container">
      <div>
        <h2 className="list-title">Working.. 🔥</h2>
        <div
          className="app-style" // 렌더링되도록 {workingLists} 변수를 받아옴
        >
          {todoLists.map((todoList) => {
            return (
              <List
                handleDelete={deleteTodoList}
                handleDone={onChangeDone}
                todoList={todoList}
                key={todoList.id}
              ></List>
            );
          })}
        </div>
      </div>
      <div>
        <h2 className="list-title">Done..! 🎉</h2>
        <div
          className="app-style" // 렌더링되도록 {workingLists} 변수를 받아옴
        >
          {DoneLists()}
        </div>
      </div>
    </div>
  );
};

export default ListContainer;
