// Action value
const SUBMIT = "SUBMIT";
const CHANGE_DONE = "CHANGE_DONE";

// Action Creator
export const submit = (title, desc) => {
  return {
    type: SUBMIT,
    title,
    desc,
  };
};

export const changeDone = (id, isDone) => {
  return {
    type: CHANGE_DONE,
    id,
    isDone,
  };
};

// Initial state
const initialState = {
  lists: [
    {
      id: 0,
      title: "리액트 공부하기",
      desc: "리액트 기초를 공부해봅시다",
      isDone: false,
    },
    {
      id: 1,
      title: "리액트 알아보기",
      desc: "리액트 기초를 알아봅시다",
      isDone: true,
    },
  ],
};

// Reducer
const todoLists = (state = initialState, action) => {
  const newTodoLists = {
    id: Date.now(),
    title: action.title,
    desc: action.desc,
    isDone: false,
  };

  // isDone 값을 바꿔주는 작업을 어떻게 할수 있을까?
  const changeDone = (id) =>
    state.lists.map((list) => {
      return { ...list, isDone: !list.isDone };
    });

  switch (action.type) {
    case "SUBMIT":
      return {
        lists: [...state.lists, newTodoLists],
      };
    case "CHANGE_DONE":
      return changeDone;
    default:
      return state;
  }
};

// 모듈파일에서는 리듀서를 export default 한다.
export default todoLists;
