import React, { useState } from "react";
import "./App.css"; // üî• Î∞òÎìúÏãú App.css ÌååÏùºÏùÑ import Ìï¥Ï§òÏïº Ìï©ÎãàÎã§.
import CustomButton from "./components/CustomButton";
import List from "./components/List";
import DoneList from "./components/DoneList";

const App = () => {
  // Îç∞Ïù¥ÌÑ∞Ïùò Î∞∞Ïó¥ÏùÑ ÎßåÎì§Ïñ¥Ï§ÄÎã§.
  // stateÎ•º ÏÇ¨Ïö©ÌïòÍ∏∞ ÏúÑÌï¥ setUsersÏôÄ useStateÎ•º ÎÑ£Ïñ¥Ï§ÄÎã§.
  const [lists, setLists] = useState([
    { id: 1, title: "Î¶¨Ïï°Ìä∏ Í≥µÎ∂ÄÌïòÍ∏∞", desc: "Î¶¨Ïï°Ìä∏ Í∏∞Ï¥àÎ•º Í≥µÎ∂ÄÌï¥Î¥ÖÏãúÎã§" },
  ]);

  const [donelists, setDoneLists] = useState([
    { id: 1, title: "Î¶¨Ïï°Ìä∏ Í≥µÎ∂ÄÌïòÍ∏∞", desc: "Î¶¨Ïï°Ìä∏ Í∏∞Ï¥àÎ•º Í≥µÎ∂ÄÌï¥Î¥ÖÏãúÎã§" },
  ]);

  // Ïù¥Í≥≥ÏùÄ nameÍ≥º ageÎ•º ÏûÖÎ†•Î∞õÏïòÏùÑÎïå stateÍ∞íÏùÑ Î∞õÏïÑÏò¨ Ìï®Ïàò
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");

  // userÎ•º Ï∂îÍ∞ÄÌï¥Ï£ºÏóàÏùÑÎïå Í∞íÏùÑ Î∞õÏïÑÏò¨ event handler ÏÉùÏÑ±
  const addListHandler = () => {
    const newLists = {
      // userÏùò Í∏∏Ïù¥Ïóê 1ÏùÑ Ï∂îÍ∞ÄÌïúÎã§. Î∞∞Ïó¥ ÎÇ¥Ïóê 4Í∞úÏùò ÏöîÏÜåÍ∞Ä ÏûàÎäîÍ≤ΩÏö∞ 5Î≤àÏß∏Î•º Ï∂îÍ∞ÄÌïúÎã§.
      id: lists.length + 1,
      title: title,
      desc: desc,
    };
    // Ï†ÑÍ∞úÏó∞ÏÇ∞ÏûêÏóê newUserÎ•º Ï∂îÍ∞ÄÌï¥Ï§ÄÎã§.
    setLists([...lists, newLists]);
  };

  const doneListHandler = () => {
    const newdoneLists = {
      // userÏùò Í∏∏Ïù¥Ïóê 1ÏùÑ Ï∂îÍ∞ÄÌïúÎã§. Î∞∞Ïó¥ ÎÇ¥Ïóê 4Í∞úÏùò ÏöîÏÜåÍ∞Ä ÏûàÎäîÍ≤ΩÏö∞ 5Î≤àÏß∏Î•º Ï∂îÍ∞ÄÌïúÎã§.
      id: donelists.length + 1,
      title: lists.title,
      desc: lists.desc,
    };
    // Ï†ÑÍ∞úÏó∞ÏÇ∞ÏûêÏóê newUserÎ•º Ï∂îÍ∞ÄÌï¥Ï§ÄÎã§.
    setDoneLists([...donelists, newdoneLists]);
  };

  // ÏÇ≠Ï†úÍ∏∞Îä•ÏùÑ ÏúÑÌïú event handler
  const deleteListHandler = (id) => {
    // user.idÍ∞Ä idÏôÄ Í∞ôÏßÄ ÏïäÎã§Î©¥ ÏÉàÎ°úÏö¥ Î∞∞Ïó¥ÏùÑ ÏÉùÏÑ±Ìï¥Ï§ÄÎã§?
    const newTodoList = donelists.filter((donelist) => donelist.id !== id);
    // ÏÉàÎ°úÏö¥ Î∞∞Ïó¥ newUserListÏùÑ ÏÉùÏÑ±Ìï¥Ï§ÄÎã§.
    setLists(newTodoList);
  };

  return (
    // mapÌï®ÏàòÎ•º ÌÜµÌï¥ Î∞∞Ïó¥Í∞íÏùÑ Î∂àÎü¨Ïò®Îã§.
    <div>
      <div className="input-style">
        <label>Ï†úÎ™©</label>
        <input
          value={title}
          // Ïù∏Ìíã Ïù¥Î≤§Ìä∏Î°ú Îì§Ïñ¥Ïò® ÏûÖÎ†• Í∞íÏùÑ nameÏùò Í∞íÏúºÎ°ú ÏóÖÎç∞Ïù¥Ìä∏
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          value={desc}
          // Ïù∏Ìíã Ïù¥Î≤§Ìä∏Î°ú Îì§Ïñ¥Ïò® ÏûÖÎ†• Í∞íÏùÑ ageÏùò Í∞íÏúºÎ°ú ÏóÖÎç∞Ïù¥Ìä∏
          onChange={(e) => setDesc(e.target.value)}
        />
        <CustomButton color="green" onClick={addListHandler}>
          Ï∂îÍ∞ÄÌïòÍ∏∞
        </CustomButton>
      </div>
      <div>Working..</div>
      <div className="app-style">
        {lists.map((list) => {
          return (
            <List
              handleDelete={deleteListHandler}
              handleDone={doneListHandler}
              list={list}
              key={list.id}
            ></List>
          );
        })}
      </div>
      <div>Done..!</div>
      <div className="app-style">
        {donelists.map((donelist) => {
          return (
            <DoneList
              handleDone={doneListHandler}
              donelist={donelist}
              key={donelist.id}
            ></DoneList>
          );
        })}
      </div>
    </div>
  );
};

export default App;
