import "./App.css"; // ðŸ”¥ ë°˜ë“œì‹œ App.css íŒŒì¼ì„ import í•´ì¤˜ì•¼ í•©ë‹ˆë‹¤.
import uuid from "react-uuid";

const App = () => {
  const [todoLists, setTodoLists] = useState([
    {
      id: 0,
      key: uuid(),
