// ReactÎ•º ÏÇ¨ÏöîÌïúÎã§. state hookÏùÑ ÏÇ¨Ïö©ÌïòÍ∏∞ ÏúÑÌï¥ { useState } Î•º Î∂àÎü¨Ïò¥
import React, { useState } from "react";
import "./App.css"; // üî• Î∞òÎìúÏãú App.css ÌååÏùºÏùÑ import Ìï¥Ï§òÏïº Ìï©ÎãàÎã§.
// id Í∞íÏùò ÌòºÏÑ†ÏùÑ Î∞©ÏßÄÌïòÍ∏∞ ÏúÑÌï¥ idÍ∞íÏúºÎ°ú uuidÎ•º ÏÑ§Ï†ïÌïòÏòÄÏùå.
// ÏûêÏÑ∏Ìïú ÎÇ¥Ïö©ÏùÄ Î≥∏Î¨∏Ïùò uuidÎ•º Ï∞∏Í≥†

const App = () => {
  const [todoLists, setTodoLists] = useState([
    // todoLists Í∞ùÏ≤¥Î°ú state hookÏùÑ ÏßÄÏ†ïÌï¥Ï£ºÏóàÏùå
    {
      // ÏàúÏÑúÎ•º ÏßÄÏ†ïÌïòÍ∏∞ ÏúÑÌï¥ idÍ∞íÏùÑ ÏßÄÏ†ïÌïòÏòÄÏùå
      id: 0,
      // idÍ∞íÎßå Í∞ÄÏßÄÍ≥† ÏûàÏùÑ Í≤ΩÏö∞ ÏßÄÏπ≠ÏÉÅÏùò ÌòºÏÑ†Ïù¥ Ïò¨Ïàò ÏûàÍ∏∞ ÎïåÎ¨∏Ïóê uuidÎ•º ÏßÄÏ†ïÌïòÏòÄÏùå
      title: "Î¶¨Ïï°Ìä∏ Í≥µÎ∂ÄÌïòÍ∏∞",
      desc: "Î¶¨Ïï°Ìä∏ Í∏∞Ï¥àÎ•º Í≥µÎ∂ÄÌï¥Î¥ÖÏãúÎã§",
      // ÏßÑÌñâÏ§ëÍ≥º ÏôÑÎ£å ÏÉÅÌÉúÎ•º ÌëúÌòÑÌïòÍ∏∞ ÏúÑÌï¥ isDone Í∞íÏùÑ true, falseÎ°ú ÏßÄÏ†ïÌïòÏòÄÏùå
      isDone: false,
    },
    {
      id: 1,
      title: "Î¶¨Ïï°Ìä∏ ÏïåÏïÑÎ≥¥Í∏∞",
      desc: "Î¶¨Ïï°Ìä∏ Í∏∞Ï¥àÎ•º ÏïåÏïÑÎ¥ÖÏãúÎã§",
      isDone: true,
    },
  ]);

  const deleteTodoListHandler = (id) => {
    // 'ÏÇ≠Ï†ú' Î≤ÑÌäºÏùÑ ÌÅ¥Î¶≠ÌñàÏùÑÎïå Ïã§ÌñâÎêòÎäî handler
    // filterÎ•º ÌÜµÌï¥ÏÑú Î∞õÏïÑÏò§Îäî idÍ∞íÍ≥º Îã§Î•∏ idÍ∞íÏùÑ Í∞ÄÏßÑ Í∞ùÏ≤¥Îì§ÎßåÏúºÎ°ú ÏÉàÎ°úÏö¥ Î∞∞Ïó¥ÏùÑ Íµ¨ÏÑ±Ìï¥ÏÑú stateÎ°ú ÎÑòÍ≤®Ï£ºÎäî Í≤É.
    const newTodoList = todoLists.filter((todoList) => todoList.id !== id);
    setTodoLists(newTodoList);
  };

  const onChangeHandler = (id) => {
    // ÏôÑÎ£å ÌòπÏùÄ Ï∑®ÏÜå Î≤ÑÌäºÏùÑ ÌÅ¥Î¶≠ÌñàÏùÑÎïå Ïã§ÌñâÎêòÎäî handler
    // Î∞õÏïÑÏò§Îäî idÍ∞íÍ≥º Í∞ôÏùÄ idÍ∞íÏùÑ Í∞ÄÏßÑ Í∞ùÏ≤¥Î•º Î∞∞Ïó¥ÏóêÏÑú Ï∞æÏïÑ Í∑∏ Ïù∏Îç±Ïä§ Í∞íÏùÑ Î¶¨ÌÑ¥ÌïòÎäî Ìï®Ïàò findIndexÎ•º ÏÑ†Ïñ∏
    const index = todoLists.findIndex((todoList) => todoList.id === id);

    // spread orderÎ°ú todoLists Î∞∞Ïó¥Ïùò Î≥µÏÇ¨Î≥∏ÏùÑ ÏÉùÏÑ±ÌïòÏòÄÏùå
    // ????    Î≥µÏÇ¨Î≥∏ÏùÄ Ïôú ÎßåÎì§Ïñ¥Ïïº Ìï†Íπå? ÏõêÎ≥∏ÏùÄ Ïñ¥ÎñªÍ≤å ÎêòÎäî Í±∏Íπå?
    // !!!!    Í∏∞Ï°¥Ïóê ÏûàÎçò Î∞∞Ïó¥Ïùò ÏÜçÏÑ±ÏùÑ Î≥ÄÍ≤ΩÌï¥Ï£ºÎ©¥ Î∂àÎ≥ÄÏÑ±Î¨∏Ï†ú ÎïåÎ¨∏Ïóê reactÏóêÏÑú ÏÜçÏÑ±Ïù¥ Î≥ÄÌïúÍ≤ÉÏùÑ Í∞êÏßÄÌïòÏßÄ Î™ªÌïòÍ≥† Î†åÎçîÎßÅÏùÑ ÌïòÏßÄ ÏïäÎäîÎã§.
    // !!!!    Í∑∏Î†áÍ∏∞ ÎïåÎ¨∏Ïóê copyTodoÎ•º ÏÉùÏÑ±Ìï¥Ï§òÏÑú Ï£ºÏÜåÍ∞íÏùÑ Î≥ÄÍ≤ΩÌï¥Ï£ºÏñ¥Ïïº ÌïúÎã§.
    const copyTodo = [...todoLists];

    // Ïù¥ Î∂ÄÎ∂ÑÏù¥ Ìï¥ÏÑùÏù¥ ÏïàÎêúÎã§.
    // ????   ...copyTodo[index] Ïù¥Í≤ÉÏùÄ Î¨¥Ïä® ÏùòÎØ∏ÏùºÍπå?
    // !!!!   ...copyTodo[index] Ïù¥Í≤ÉÏùÄ copyTodo[index] ÎÇ¥Ïùò Î™®Îì† ÏÜçÏÑ±ÏùÑ Î≥µÏÇ¨Ìï¥Ï§ÄÎã§Îäî ÏùòÎØ∏Ïù¥Îã§.
    // ????   spread syntaxÏùò Ï†ïÌôïÌïú ÏùòÎØ∏Îäî Î¨¥ÏóáÏùºÍπå?
    // !!!!   spread syntaxÎäî Î∞∞Ïó¥ Í∞íÏùÑ Î≥µÏÇ¨Ìï¥Ïò®Îã§.
    if (copyTodo[index].isDone === true) {
      copyTodo[index] = { ...copyTodo[index], isDone: false };
    } else {
      copyTodo[index] = { ...copyTodo[index], isDone: true };
    }

    // setTodoLists Î°ú ÏÉàÎ°úÏö¥ Î∞∞Ïó¥ÏùÑ Î≥µÏÇ¨Ìï¥Ï§ÄÎã§.
    setTodoLists(copyTodo);
  };

  const workingLists = todoLists
    .filter((todoList) => todoList.isDone === false)
    .map((todoList) => {
      return (
        <List
          handleDelete={deleteTodoListHandler}
          handleDone={onChangeHandler}
          todoList={todoList}
          id={todoList.id}
        ></List>
      );
    });

  const doneLists = todoLists
    .filter((todoList) => todoList.isDone === true)
    .map((todoList) => {
      return (
        <List
          handleDelete={deleteTodoListHandler}
          handleDone={onChangeHandler}
          todoList={todoList}
          id={todoList.id}
        ></List>
      );
    });

  // ÏÉàÎ°úÏö¥ titleÍ≥º descÎ•º Î∞õÏïÑ ÏÉàÎ°úÏö¥ Í∞ùÏ≤¥Î•º ÎßåÎì§Í∏∞ ÏúÑÌï¥ state hookÏùÑ ÏÑ†Ïñ∏Ìï¥Ï§å
