// Action value
const SUBMIT = "SUBMIT";

// Action Creator
export const submit = (title, desc) => {
  return {
    type: SUBMIT,
    title,
    desc,
  };
};

// Initial state
const initialState = {
  lists: [
    {
      id: 0,
      title: "리액트 공부하기",
      desc: "리액트 기초를 공부해봅시다",
      isDone: false,
    },
    {
      id: 1,
      title: "리액트 알아보기",
      desc: "리액트 기초를 알아봅시다",
      isDone: true,
    },
  ],
};

const newTodoLists = {
  id: Date.now(),
  title: title,
  desc: desc,
  isDone: false,
};

// Reducer
const addTodoList = (state = initialState, action) => {
  switch (action.type) {
    case "SUBMIT":
      return {
        lists: [...state.lists, newTodoLists],
      };

    default:
      return state;
  }
};

// 모듈파일에서는 리듀서를 export default 한다.
export default addTodoList;
