import React, { useState } from "react";
import "./App.css"; // üî• Î∞òÎìúÏãú App.css ÌååÏùºÏùÑ import Ìï¥Ï§òÏïº Ìï©ÎãàÎã§.

function List(props) {
  console.log(props);
  return (
    <div className="square-style">
      <h2>{props.todoList.title}</h2>
      <div>{props.todoList.desc}</div>
      <div className="button-set">
        <button
          className="todo-delete-button button"
          onClick={() => {
            props.handleDelete(props.todoList.id);
          }}
        >
          ÏÇ≠Ï†ú
        </button>
        <button
          className="todo-complete-button button"
          onClick={() => {
            props.handleDone(props.todoList.id);
          }}
        >
          ÏôÑÎ£å
        </button>
      </div>
    </div>
  );
}

const App = () => {
  const [todoLists, setTodoLists] = useState([
    {
      id: 0,
      title: "Î¶¨Ïï°Ìä∏ Í≥µÎ∂ÄÌïòÍ∏∞",
      desc: "Î¶¨Ïï°Ìä∏ Í∏∞Ï¥àÎ•º Í≥µÎ∂ÄÌï¥Î¥ÖÏãúÎã§",
      isDone: false,
    },
    {
      id: 1,
      title: "Î¶¨Ïï°Ìä∏ ÏïåÏïÑÎ≥¥Í∏∞",
      desc: "Î¶¨Ïï°Ìä∏ Í∏∞Ï¥àÎ•º ÏïåÏïÑÎ¥ÖÏãúÎã§",
      isDone: true,
    },
  ]);

  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");

  const onSubmitHandler = () => {
    if (title !== "" && desc !== "") {
      const newTodoLists = {
        id: todoLists.length + 1,
        title: title,
        desc: desc,
        isDone: false,
      };

      setTodoLists([...todoLists, newTodoLists]);
    } else {
      return null;
    }
  };

  const onChangeHandler = (id) => {
    setTodoLists(
      todoLists.map((todoList) =>
        todoList.id === id
          ? { ...todoLists, isDone: !todoList.isDone }
          : todoList
      )
    );
  };

  const deleteTodoListHandler = (id) => {
    const newTodoList = todoLists.filter((todoList) => todoList.id !== id);
    setTodoLists(newTodoList);
  };

  return (
    <div class="layout">
      <div className="container">
        <div>My Todo List</div>
        <div>React</div>
      </div>
      <div className="add-form">
        <div className="input-style">
          <label className="form-label">Ï†úÎ™©</label>
          <input
            className="add-input"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <label className="form-label">ÎÇ¥Ïö©</label>
          <input
            className="add-input"
            value={desc}
            onChange={(e) => setDesc(e.target.value)}
          />
        </div>
        <button className="add-button" onClick={onSubmitHandler}>
          Ï∂îÍ∞ÄÌïòÍ∏∞
        </button>
      </div>
      <div className="list-container">
        <h2 className="list-title">Working.. üî•</h2>
        <div className="app-style">
          {todoLists.map((todoList) => {
            if (todoList.isDone === false) {
              return (
                <List
                  handleDelete={deleteTodoListHandler}
                  handleDone={onChangeHandler}
                  todoList={todoList}
                  key={todoList.id}
                ></List>
              );
            }
          })}
        </div>
        <h2 className="list-title">Done..! üéâ</h2>
        <div className="app-style"></div>
      </div>
    </div>
  );
};

export default App;
