import React, { useState } from "react";
import "./App.css"; // 🔥 반드시 App.css 파일을 import 해줘야 합니다.

const App = () => {
  const [todoLists, setTodoLists] = useState([
    {
      id: 0,
      title: "리액트 공부하기",
      desc: "리액트 기초를 공부해봅시다",
      isDone: false,
    },
    {
      id: 1,
      title: "리액트 알아보기",
      desc: "리액트 기초를 알아봅시다",
      isDone: true,
    },
  ]);

  const workingLists = todoLists
    .filter((todoList) => todoList.isDone === false)
    .map((todoLIst) => {
      return (
        <List
          handleDelete={deleteTodoListHandler}
          handleDone={onChangeHandler}
          todoList={todoList}
          key={todoList.id}
        ></List>
      );
    });

  const doneLists = todoLists
    .filter((todoList) => todoList.isDone === true)
    .map((todoLIst) => {
      return (
        <List
          handleDelete={deleteTodoListHandler}
          handleDone={onChangeHandler}
          todoList={todoList}
          key={todoList.id}
        ></List>
      );
    });

  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");

  const onSubmitHandler = () => {
    if (title !== "" && desc !== "") {
      const newTodoLists = {
        id: todoLists.length + 1,
        title: title,
        desc: desc,
        isDone: false,
      };

      setTodoLists([...todoLists, newTodoLists]);
    } else {
      return null;
    }
  };

  const onChangeHandler = (id) => {
    setTodoLists(
      todoLists.map((todoList) =>
        todoList.id === id
          ? { ...todoLists, isDone: !todoList.isDone }
          : todoList
      )
    );
  };

  const deleteTodoListHandler = (id) => {
    const newTodoList = todoLists.filter((todoList) => todoList.id !== id);
    setTodoLists(newTodoList);
  };

  return (
    <div class="layout">
      <div className="container">
        <div>My Todo List</div>
        <div>React</div>
      </div>
      <div className="add-form">
        <div className="input-style">
          <label className="form-label">제목</label>
          <input
            className="add-input"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <label className="form-label">내용</label>
          <input
            className="add-input"
            value={desc}
            onChange={(e) => setDesc(e.target.value)}
          />
        </div>
        <button className="add-button" onClick={onSubmitHandler}>
          추가하기
        </button>
      </div>
      <div className="list-container">
        <IsDone />
      </div>
    </div>
  );
};

